import posts from './data/tweets.json';

const initialState = {
    tweets: posts
}

const tweets = (state = initialState, action) => {
    switch (action.type) {
        case 'create-tweet':
            const tweet = {
                _id: (new Date()).getTime() + '',

                ...action.tweet,


            };

            return ({
                tweets: [
                    tweet,
                    ...state.tweets,
                ]
            });

            break;
        default:
            return(state);
    }
};
export default tweets;
