import posts from './data/tweets.json';

const initialState = {
    tweets: posts
}

const tweets = (state = initialState, action) => {
    switch (action.type) {
        case 'create-tweet':
            const tweet = {
                    _id: (new Date()).getTime() + '',
                    "name": "Elon Musk", "userName": "elonmusk", "profile_image": "/images/elon_dp.jpeg", "time": "23h",
                    ...action.tweet,


                    "image": "/images/inspiration4.jpeg",
                    "image_title" : "Countdown: Inspiration4 Mission to Space | Netflix Official Site",
                    "image_description":"From training to launch to landing, this all-access docuseries rides along with the Inspiration4 crew on the first all-civilian orbital space",
                    "website": "netflix.com",
                    "comments": "4.2K", "reshare": "3.5K", "likes":"37.5K",};

            return([
                {
                    ...tweet,
                    "tweet": action.tweet
                },
                ...state
            ]);

            break;
        default:
            return(state);
    }
};
export default tweets;
