import React from "react";
import {Dimensions} from 'react-native';
const ProfileInfo = ( person = {
            firstName: 'Jose',
            lastName: 'Annunziato',
            handle: 'jannunzi',
            profilePicture: 'jose.png',
            bannerPicture: 'polyglot.png',
            bio: 'Faculty, Software Engineer, AI, Space, and renewable enthusiast. Retweets and likes are not endorsements.',
            website: 'youtube.com/webdevtv',
            location: 'Boston, MA',
            dateOfBirth: '7/7/1968',
            dateJoined: '4/2009',
            followingCount: 312,
            followersCount: 180,

    })  => {

        const width = Dimensions.get('window');
        const ratio = 882 / 1233;

        const style = {
            width,
            height: width * ratio,
        }
    return(<div>
        <div className="row">
            <div className="col-2 mt-3 ps-5">
                <i className="fas fa-long-arrow-alt-left" style={{fontSize:"24px", color:"white"}}></i>
            </div>
            <div className="col-10">
                <h5 className = "mb-0"><b>{person.firstName} {person.lastName}</b></h5>
                <label>5,196 Tweets</label>
            </div>
        </div>
            <img src = "\images\cover_image.png" style={style} resizeMode="contain" ></img>



    </div>
    );
}
export default ProfileInfo;
