import React, {useEffect} from "react";
import {useState} from 'react';
import {useSelector} from "react-redux";

const selectProfile = (state) => state.profile;

const EditProfile = ()  => {
    const person = useSelector(selectProfile);

    const dispatch = useDispatch();


    const[firstName,setFirstName] = useState(person.firstName);
    const[lastName,setLastName] = useState(person.lastName);
    const[bio,setBio] = useState(person.bio);
    const[location,setLocation] = useState(person.location);
    const[website,setWebsite] = useState(person.website);
    const[dateOfBirth, setDateOfBirth] = useState(person.dateOfBirth);

    var datestring = new Date(person.dateOfBirth).toString().split(" ");
    var month = datestring[1]
    var date = datestring[2]
    var year = datestring[3]

    const handleNameChange=(event)=>{
        const newValue = event.target.value;
        setFirstName(newValue)
    }

    return(<div className="mb-2">
            <div className="row mb-2">
                <div className="col-1 mt-1 ps-3">
                    <i className="fas fa-times" style={{fontSize:"18px", color:"white"}}></i>
                </div>
                <div className="row col-8">
                    <label style={{color:"white", fontSize:"20px"}}><b>Edit profile</b></label>
                </div>
                <div className="row col-2 ms-5">
                    <button type="button" className="btn rounded-pill text-nowrap p-1 ms-3" style={{float:"right", background:"white", fontSize:"14px", color:"black"}}>
                        Save
                    </button>
                </div>
            </div>
            <div className="mb-2">
                <img src ={person.bannerPicture} style={{width:"100%", height: "200px"}}></img>
            </div>
            <div className="row mb-0">
                <div className="col-7" style={{height:"45px"}}>
                    <img src={person.profilePicture} className="rounded-circle" style={{height:"100px", width:"100px", objectFit:"cover", position:"relative", top:"-60px", left:"15px", border:"solid black 3px"}} alt="profile pic"></img>
                </div>
            </div>
            <form className="ps-3 pe-3">
                <input onChange={handleNameChange} type="text" value={firsName}/>
                <textarea className="form-control bg-transparent outline-secondary mb-3" rows="1" style={{border:"1px solid gray", resize: "none"}}>
                        First Name
                </textarea>
                <textarea className="form-control bg-transparent outline-secondary mb-3" rows="1" style={{border:"1px solid gray", resize: "none"}}>
                        Last Name
                </textarea>
                <textarea className="form-control bg-transparent outline-secondary mb-3" rows="3" style={{border:"1px solid gray", resize: "none"}}>
                        Bio
                </textarea>
                <textarea className="form-control bg-transparent outline-secondary mb-3" rows="1" style={{border:"1px solid gray", resize: "none"}}>
                        Location
                </textarea>
                <textarea className="form-control bg-transparent outline-secondary mb-3" rows="1" style={{border:"1px solid gray", resize: "none"}}>
                        Website
                </textarea>
                <label>Birth date</label> &middot; <a href='\#'>Edit</a>
                <span style={{display:"block"}}>{month} {date}, {year}</span>
            </form>
        </div>
    );
}
export default EditProfile;
